UPDATE publications SET date_year = '2016', protocol_ids = '121802', study_name = 'Pediatric Imaging, Neurocognition, and Genetics', study_acronym = 'PING', study_type = 'Self Report', disease_phenotype = 'Anxiety Disorders', primary_research_focus = 'Anxiety is a risk factor for many adverse neuropsychiatric and socioeconomic outcomes, and has been linked to functional and structural changes in the ventromedial prefrontal cortex (VMPFC). However, the nature of these differences, as well as how they develop in children and adolescents, remains poorly understood. More effective interventions to minimize the negative consequences of anxiety require better understanding of its neurobiology in children. Recent research suggests that structural imaging studies may benefit from clearly delineating between cortical surface area and thickness when examining these associations, as these distinct cortical phenotypes are influenced by different cellular mechanisms and genetic factors. The present study examined relationships between cortical surface area and thickness of the VMPFC and a self-report measure of anxiety (SCARED-R) in 287 youths aged 720 years from the Pediatric Imaging, Neurocognition, and Genetics (PING) study.', funding_source = 'NICHD NIH HHS,NIGMS NIH HHS,NCATS NIH HHS,NIMH NIH HHS,NICHD NIH HHS,NICHD NIH HHS,NIDA NIH HHS', award_num = 'U54 HD079123,R01 GM104400,UL1 TR001863,R01 MH083320,U54 HD090255,R01 HD061414,RC2 DA029475', foa = 'PA-18-591/RFA-HD-14-012,RFA-GM-14-006,PAR-15-304/PA-16-328/PA-16-288,PA-13-302/PA-11-260/PA-07-070,RFA-HD-16-013/PA-18-591,PA-10-067,RFA-OD-09-004' WHERE id = 230; 
